import React, { useEffect, useState } from 'react';


function Weather() {

    const [weather, setWeather] = useState([]);
    const [bgImage, setBgImage] = useState();
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        getWeather();
    }, []);

    const getWeather = async () => {
        await navigator.geolocation.getCurrentPosition(
            function (position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                fetchWeather(lat, lon);
            }
        )
    }

    const fetchWeather = async (lat, lon) => {
        const key = `157a086678c6a4bddb37252236346531`;
        const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
        try {
            const apiWeather = await fetch(userLocation);
            const data = await apiWeather.json();
            setWeather(data);
            setLoading(false);
            setBgImage('../assets/weather/' + data.weather[0].icon);
            console.log(bgImage);

        } catch (err) {
            console.error(err)
        }
    }
    if (loading) {
        return (
            <div id="weather">
                <h1>Loading...</h1>
            </div>
        );
    };

    return (
        <div id="weather" style={{ backgroundImage: "url(" + bgImage + ")" }}>
            <h1>{weather.name}</h1>
            <h1>{Math.floor(weather.main.temp - 273.15)} Â°C</h1>
            <h1>{weather.weather[0].icon}.jpg</h1>
        </div >
    );
}

export default Weather;
