import React, { useEffect } from 'react';

function Weather() {

    useEffect(() => {
        getWeather();
    }, [])



    const getWeather = () => {
        const gps = navigator.geolocation.getCurrentPosition(
            function (position) {
                const userLocation = "api.openweathermap.org/data/2.5/weather?lat=" + position.coords.latitude + "&lon=" + position.coords.longitude + "&appid=157a086678c6a4bddb37252236346531"
                const city = fetch(userLocation).then(response => response.json())
                    .then(response => {
                        console.log(response);
                    })
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                console.log(Math.round(lat))
            });
    }
    return (
        <div>
            Pogoda!
        </div>
    );
}

export default Weather;
