import React, { useEffect, useState } from 'react';

function Weather() {

    const [weather, setWeather] = useState([]);
    const [temperature, setTemperature] = useState([]);
    const [weatherDescription, setWeatherDescription] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        getWeather();
    }, []);

    const getWeather = () => {
        navigator.geolocation.getCurrentPosition(
            function (position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                fetchWeather(lat, lon);
            }
        )
    }

    const fetchWeather = async (lat, lon) => {
        const key = `157a086678c6a4bddb37252236346531`;
        const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
        try {
            const apiWeather = await fetch(userLocation);
            const data = apiWeather.json();
            setWeather(data);

        } catch (err) {
            console.error(err)
        }
    }
    return (
        <div id="weather">
            <h1>{weather.name}</h1>
            {/* <h1>{Math.floor(temperature.temp - 273.15)} Â°C</h1> */}
        </div>
    );
}

export default Weather;
