import React, { useEffect, useState } from 'react';

function Weather() {

    const [weather, setWeather] = useState([]);
    const [temperature, setTemperature] = useState([]);

    useEffect(() => {
        getWeather();
    }, []);




    const getWeather = () => {
        navigator.geolocation.getCurrentPosition(
            async function (position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                const key = `157a086678c6a4bddb37252236346531`;
                const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
                const city = await fetch(userLocation);
                const json = await city.json();
                setWeather(json);
                setTemperature(json.main);
            }
        )

    }
    return (
        <div>
            <h1>{weather.name}</h1>
            <h1>{weather.weather.main[0]}</h1>
            <h1>{Math.floor(temperature.temp - 273.15)} Â°C</h1>
        </div>
    );
}

export default Weather;
