import React, { useEffect, useState } from 'react';

function Photo() {

    const [image, setImage] = useState([]);

    useEffect(() => {
        fetchPhoto();
    }, [])



    const fetchPhoto = async () => {
        const randId = await Math.floor(Math.random() * (14 - 1 + 1)) + 1;
        const photoURL = `https://pixabay.com/api/?key=16806694-8789b173812b34e350141a20b&category=nature&image_type=photo`;

        const photoFetch = await fetch(photoURL);
        const data = await photoFetch.json();
        setImage(data.hits[randId]);
        console.log(data.hits[randId])
    }


    return (
        <div id="photo" style={{ backgroundImage: `url(${image.largeImageURL})` }} >
            <div className="about">
                <p>Author: {image.user}</p>
                <p>Author: {image.user}</p>
            </div>
        </div>

    );
}

export default Photo;
