import React, { useState } from 'react';
import Popup from './Popup';

function Form() {

    const [task, setTask] = useState('');
    const [taskCopy, setTaskCopy] = useState(JSON.parse(localStorage.getItem('data')));

    const taskValue = (e) => {
        setTask(e.target.value);
    }

    const handleClick = (e) => {
        e.preventDefault();
        setTask('');
        addTask(task);
    }

    const addTask = (task) => {
        if (task === '') {
            Popup('Fill Data üñê');
        } else {
            const copy = [...taskCopy];
            copy.push({
                task: task,
                status: "primary"
            });
            localStorage.setItem('data', JSON.stringify(copy));
            setTaskCopy(copy);
            console.log(taskCopy);
        };
    }


    return (
        <div className="form">
            <form action="" onSubmit={handleClick}>
                <input type="text"
                    name="task"
                    placeholder="Write Your Task"
                    value={task}
                    autoComplete="off"
                    onChange={(e) => taskValue(e)}
                />
                <button type="submit">Create it!</button>
            </form>
        </div>

    );
}

export default Form;
