import React, { useEffect, useState } from 'react';

function Weather() {

    const [weather, setWeather] = useState([]);
    const [temperature, setTemperature] = useState([]);

    useEffect(() => {
        getWeather();
    }, []);

    useEffect(() => {
        console.log(weather.main);
        setTemperature(weather.main);
    }, [weather]);




    const getWeather = () => {
        navigator.geolocation.getCurrentPosition(
            async function (position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                const key = `157a086678c6a4bddb37252236346531`;
                const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
                const city = await fetch(userLocation);
                const json = await city.json();
                setWeather(json);
                console.log(json.main.temp)
            }
        )

    }
    return (
        <div>
            <h1>{weather.name}</h1>
        </div>
    );
}

export default Weather;
