import React, { useState } from 'react';

import '../styles/taskList.css';
import { faTrashAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import data from '../data/data.json'


function TaskList() {

    const [status, setStatus] = useState(false);


    const changeStatus = (idx) => {
        const tasks = document.querySelectorAll('.task')[idx];
        console.log(idx)
        setStatus(!status);
        if (status === true) {
            tasks.style.borderLeft = '10px solid var(--primary)';
        } else {
            tasks.style.borderLeft = '10px solid var(--success)';
        }
    }


    return (
        data.map((task, idx) => (
            <div className="task" key={idx} onClick={changeStatus(idx)}>
                <h1>{task.task}</h1>
                <div className="icons">
                    <FontAwesomeIcon icon={faTrashAlt} />
                </div>
            </div>

        ))
    );
}

export default TaskList;
