import React, { useEffect, useState } from 'react';

function Weather() {

    const [weather, setWeather] = useState([]);

    useEffect(() => {
        getWeather();
    }, [weather])

    useEffect(() => {

        console.log(weather);
    },
        [weather] //This is dependency and it will run only when data is changed
    )




    const getWeather = () => {
        navigator.geolocation.getCurrentPosition(
            async function (position) {
                const lat = position.coords.latitude;
                const lon = position.coords.longitude;
                const key = `157a086678c6a4bddb37252236346531`;
                const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
                const city = await fetch(userLocation);
                const json = await city.json();
                console.log(json.weather);
                setWeather(json.weather);
            }
        )

    }
    return (
        <div>

        </div>
    );
}

export default Weather;
