import React, { useState, useEffect } from 'react';

import '../styles/taskList.css';
import { faTrashAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import data from "../data/data.json"

function TaskList() {
    const [dataTask, setDataTask] = useState([]);

    useEffect(() => {
        tasks();
    }, [])


    const dataValue = JSON.parse(localStorage.getItem('data'));
    const tasks = () => {
        if (dataValue === null) {
            setDataTask(data)
        }
        setDataTask(dataValue);
    }

    const changeStatus = (index) => {
        const copyTask = [...dataTask];
        if (copyTask[index].status === 'success') {
            copyTask[index].status = 'primary';
        } else {
            copyTask[index].status = 'success';
        }
        setDataTask(copyTask);
        localStorage.setItem('data', JSON.stringify(copyTask));
    }

    const deleteTask = (index) => {
        const divTask = document.querySelectorAll(".task")[index];
        divTask.classList.add('hidden');
        divTask.addEventListener('transitionend', function (e) {
            divTask.classList.add('remove');

            const copyTask = [...dataTask];
            copyTask.splice(index, 1);
            setDataTask(copyTask);

            localStorage.setItem('data', JSON.stringify(copyTask));

            const popup = document.createElement('div');
            popup.innerHTML = "Deleted ✅"
            popup.classList.add('popup');
            document.body.appendChild(popup);

            setTimeout(() => {
                popup.parentNode.removeChild(popup);
            }, 1000)



        }, {
            capture: false,
            once: true,
            passive: false
        });


    }


    return (

        dataTask.map((task, index) => (
            <div
                className={"task " + task.status}
                key={index}>
                <h1 onClick={() => changeStatus(index)}>{task.task}</h1>
                <div className="icons" onClick={() => deleteTask(index)}>
                    <FontAwesomeIcon icon={faTrashAlt} />
                </div>
            </div>
        ))

    )

}

export default TaskList;
