import React, { useEffect, useState } from 'react';

import '../styles/news.css';
import newsImg from '../assets/news/news.svg';

import { faSpinner } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";



function News() {

    const [news, setNews] = useState([
        {
            "title": "A Living Example",
            "description": "City Councilmember Dr. Shahid Shafi Exemplifies The American Dream.",
            "url": "https://www.southlakestyle.com/community/people/a-living-example/",
            "image": "https://lh6.googleusercontent.com/proxy/y6oUWR3-7B3gA3yE5gb6IQLnIUK5OePriC...",
            "publishedAt": "2019-06-28 09:32:37 UTC",
            "source": {
                "name": "Southlake Style",
                "url": "https://www.southlakestyle.com"
            }
        },
        {
            "title": "A Living Example",
            "description": "City Councilmember Dr. Shahid Shafi Exemplifies The American Dream.",
            "url": "https://www.southlakestyle.com/community/people/a-living-example/",
            "image": "https://lh6.googleusercontent.com/proxy/y6oUWR3-7B3gA3yE5gb6IQLnIUK5OePriC...",
            "publishedAt": "2019-06-28 09:32:37 UTC",
            "source": {
                "name": "Southlake Style",
                "url": "https://www.southlakestyle.com"
            }
        }
    ]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchNews();
    }, []);


    const fetchNews = async () => {
        const token = 'a05805ed58ffc71ae2f0a4ea2d84ab25';
        const newsURL = `https://gnews.io/api/v3/top-news?lang=pl&max=3&token=${token}`;
        try {
            let fetchData = await fetch(newsURL);
            let data = await fetchData.json().then(() => {
                setLoading(false);
            });
            setNews(data.articles);
        } catch (err) {

        }
    }

    if (loading) {
        return (
            <div id="news">
                <FontAwesomeIcon icon={faSpinner} className="fa-spin" />
            </div>
        );
    };



    return (
        <div id="news">
            <div id="news-title">
                <h1>News</h1>
                <img src={newsImg} alt="news icon" title="News" />
            </div>
            {
                news.map((article, index) => (
                    <div key={index} className="article">
                        <h2><a href={article.url} target="blank">{article.title}</a></h2>
                        <p>{article.publishedAt}</p>
                    </div>
                ))
            }
        </div>
    );
}

export default News;
