import React, { useEffect, useState } from 'react';

import { faSpinner } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


function Weather() {

    const [weather, setWeather] = useState([]);
    const [image, setImage] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        getWeather();
    }, []);

    const getWeather = async () => {
        if (navigator.geolocation) {
            await navigator.geolocation.getCurrentPosition(
                function (position) {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;
                    fetchWeather(lat, lon);
                }, function (positionError) {
                    console.log(positionError);
                }
            )
        }
    }

    const fetchWeather = async (lat, lon) => {
        const key = `157a086678c6a4bddb37252236346531`;
        const userLocation = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${key}`;
        try {
            const apiWeather = await fetch(userLocation);
            const data = await apiWeather.json();
            setWeather(data);
            setLoading(false);
            setImage(`http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`);
        } catch (err) {
            console.error(err)
        }
    }
    if (loading) {
        return (
            <div id="weather">
                <FontAwesomeIcon icon={faSpinner} className="fa-spin" />
            </div>
        );
    };



    return (
        <div id="weather">
            <h1>{weather.name}</h1>
            <img src={image} />
            <h1>{Math.floor(weather.main.temp - 273.15)} Â°C</h1>
        </div >
    );
}

export default Weather;
