import React from 'react';

import DarkModeSwitch from './DarkModeSwitch';
import '../styles/nav.css';

function Nav() {

        const handleClick = (e) => {
            e.preventDefault();
            console.log(e);
          }

    return (
        <nav>
            <div className="logo">
                <h1>Your Tasks</h1>
            </div>
            <div className="form">
                <form action="" onSubmit={handleClick}>
                    <input type="text" placeholder="Write your Task" name="task" autoComplete="off"/>
                    <button type="submit">Create it!</button>
                </form>
            </div>
            <DarkModeSwitch />
        </nav>
    );
}

export default Nav;
