import React, { useEffect } from 'react';


import '../styles/popup.css';

function Popup({ tekst }) {

    const popup = document.querySelector('.popup');

    setTimeout(() => {
        popup.parentNode.removeChild(popup);
    }, 1000)

    return (
        <div className="popup">
            {tekst}
        </div>
    );
}

export default Popup;
