import React, { useState, createContext, useEffect } from 'react';

export const TaskContext = createContext();

export const TaskProvider = (props) => {
    useEffect(() => {
        tasks();
    }, []);

    const [dataTask, setDataTask] = useState(JSON.parse(localStorage.getItem('data')));



    const tasks = () => {
        let dataValue = JSON.parse(localStorage.getItem('data'));
        if (dataValue === null) {
            dataValue = [{
                task: "Have Fun with my TaskManager ✨",
                status: "success"
            }];
            localStorage.setItem('data', JSON.stringify(dataValue));
        }
        setDataTask(dataValue);
    }

    return (
        <TaskContext.Provider value={[dataTask, setDataTask]} >
            {props.children}
        </TaskContext.Provider >
    );
}
