import React, { useState, useEffect } from 'react';

import '../styles/taskList.css';
import { faTrashAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import data from '../data/data.json'


function TaskList() {

    const [status, setStatus] = useState(false);

    const changeStatus = (index, status) => {
        const task = document.querySelectorAll('.task')[index];
        task.classList.remove(status);
        status === 'success' ? task.classList.add = 'primary' : task.classList.add = "success";
    }


    return (
        data.map((task, index) => (
            <div
                className={"task " + task.status}
                key={index}
                onClick={() => changeStatus(index, task.status)}
            >

                <h1>{task.task}</h1>
                <div className="icons">
                    <FontAwesomeIcon icon={faTrashAlt} />
                </div>

            </div>

        ))
    );
}

export default TaskList;
