import React, { useState } from 'react';

function Form() {

    const [task, setTask] = useState();

    const taskValue = (e) => {
        setTask(e.target.value);
    }

    const handleClick = (e) => {
        e.preventDefault();
        console.log(task);
        setTask('');
    }

    return (
        <div className="form">
            <form action="" onSubmit={handleClick}>
                <input type="text"
                    name="task"
                    placeholder="Write Your Task"
                    defaultValue={task}
                    autoComplete="off"
                    onKeyUp={(e) => taskValue(e)}
                />
                <button type="submit">Create it!</button>
            </form>
        </div>

    );
}

export default Form;
