import React, { useEffect, useState } from 'react';

function Photo() {

    const [image, setImage] = useState([
        { "largeImageURL": "https://pixabay.com/get/53e2d442435aad14f6da8c7dda7936781d3fd8e35a556c4870267bdc9544cd59b1_1280.jpg" }
    ]);

    useEffect(() => {
        fetchPhoto();
    }, [])



    const fetchPhoto = async () => {
        const randId = await Math.floor(Math.random() * (20 - 1 + 1)) + 1;
        const photoURL = `https://pixabay.com/api/?key=16806694-8789b173812b34e350141a20b&category=nature&image_type=photo`;
        try {
            let photoFetch = await fetch(photoURL);
            let data = await photoFetch.json();
            setImage(data.hits[randId]);
        } catch (err) {
            console.log(err);
        }
        console.log(data.hits[randId])
    }


    return (
        <div id="photo" style={{ backgroundImage: `url(${image.largeImageURL})` }} >

        </div>

    );
}

export default Photo;
